public class HourlyEmployee extends Employee { private double hours; public HourlyEmployee(String name, String address, String phone, String ssn, double rate) { super(name, address, phone, ssn, rate); this.hours = 0; } public double pay() { return this.rate * this.hours; } public String toString() { return super.toString() + "\nCurrent Hours: " + this.hours; } public void addHours(int moreHours) { this.hours += moreHours; } }
